    project=this_project.name,
    display_name=this_project.display_name.apply(lambda display_name: f"{display_name} Front Page"),
    description="Front page service",
    labels=[
        nobl9.ServiceLabelArgs(
            key="env",
            values=[
                "dev",
                "prod",
            ],
        ),
        nobl9.ServiceLabelArgs(
            key="team",
            values=["red"],
        ),
    ])
