	pulumi.Run(func(ctx *pulumi.Context) error {
		thisProject, err := nobl9.NewProject(ctx, "thisProject", &nobl9.ProjectArgs{
			DisplayName: pulumi.String("My Project"),
			Description: pulumi.String("An example N9 Terraform project"),
		})
		if err != nil {
